from Crypto.Util.number import long_to_bytes
import math

def fermat_factor(n):
    a = math.isqrt(n) + 1
    while True:
        b_squared = a * a - n
        b = math.isqrt(b_squared)
        if b * b == b_squared:
            return (a - b, a + b)
        a += 1

